import React from 'react';
import "../css/Post.css";

const Post = () => {
    return (
        <div className="post">
            <h3>Username</h3>
            {/*header -> avatar + username*/}

            {/* image */}
            <img
            className="post__image"
             src="https://th.bing.com/th/id/OIP.K1PhJ0Q5jEqpd9HbNAwfHwAAAA?pid=Api&rs=1" alt=""/>

            {/* username + caption */}
            <h4>Username: caption</h4>
        </div>
    )
}

export default Post
