       <img
       className="app__headerImage"

      src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
       alt=""
       />
     </div>

     <h1>hello</h1>
     {posts.map(({id, post}) => (
       <Post 
       key={id}
       username={post.username}
       caption={post.caption}
       imageUrl={post.imageUrl}
       />
     ))}

    </div>
  );
}

export default App;
