import React from 'react';
import "../css/Post.css";
import Avatar from "@material-ui/core/Avatar";

const Post = () => {
    return (
        <div className="post">
            <Avatar
            className="post__avatar"
            alt="Ram"
            src="static/images/avatar/1.jpg"
            />
            <h3>Username</h3>
            {/*header -> avatar + username*/}

            {/* image */}
            <img
            className="post__image"
             src="https://th.bing.com/th/id/OIP.K1PhJ0Q5jEqpd9HbNAwfHwAAAA?pid=Api&rs=1" alt=""/>

            {/* username + caption */}
            <h4 className="post__text"><strong>Ram</strong> Have a nice day</h4>
        </div>
    )
}

export default Post
